import axios from 'axios';
import React, { Component } from 'react';
import AppBody from '../../../../components/AppBody';
import helper from '../../../../utils/helper';
const user = helper.user();
class UserUpdateProfile extends Component {

    constructor(props) {
        super(props);
        this.state = {
            name: '',
            email: '',
            phone: '',
            image: '',
            userDetails: [],
            loading: false,
        }
    }
    componentDidMount() {
        this.setState({ loading: true });
        axios.get('http://indonews.live/api/profile', {
            headers: { "Authorization": `Bearer ${user.access_token}` }
        }).then(res => {
            this.setState({ userDetails: res.data, loading: false });
        }).catch(err => {
            console.log(err);
            this.setState({ loading: false });
        })
    }

    inputChangeHandler(e) { this.setState({ [e.target.name]: e.target.value }) }

    updateProfileHandler = async (e) => {
        const { name, email, phone } = this.state;
        this.setState({ loading: true })
        try {
            axios.post('http://indonews.live/api/update-profile', {
                headers: { "Authorization": `Bearer ${user.access_token}` },
                name: name,
                email:email,
                mobile_no: phone,
            }).then(res => {
                this.setState({ loading: false })
                console.log(res)
            }).catch(err => {
                this.setState({ loading: false })
            })
        } catch (error) {
            console.log(error)
        }
    }

    render() {
        const { userDetails } = this.state
        return (
            <AppBody
                loader={this.state.loading}
                content={
                    <>
                        <div className="container">
                            <div className="row">
                                <div className="col-md-3"></div>
                                <div className="col-md-6">
                                    <div className="card mt-5 mb-5 p-5">
                                        <div className="form-group text-center">
                                            <img className="img" src={userDetails.profile_pic} style={{ backgroundColor: 'white', border: '1px solid black', borderRadius: '100px', height: '200px', width: '200px', cursor: 'pointer' }} onClick={this.imageClickHandler} />
                                            <input type="file" name="image" id="updateImage" onChange={e => { this.inputChangeHandler(e) }} />
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="name">Name</label>
                                            <input type="text" className="form-control" placeholder="Name" name="name" id="name" value={userDetails.name} onChange={e => { this.inputChangeHandler(e) }} />
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="email">Email</label>
                                            <input type="text" className="form-control" name="email" placeholder="Email" id="email" value={userDetails.email} onChange={e => { this.inputChangeHandler(e) }} />
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="phone">Mobile No.</label>
                                            <input type="text" className="form-control" id="phone" name="phone" placeholder="Phone" value={userDetails.mobile_no} onChange={e => { this.inputChangeHandler(e) }} />
                                        </div>
                                        <div className="form-group">
                                            <button className="form-control btn btn-warning" onClick={this.updateProfileHandler}>Update</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </>
                }

            />
        )
    }
}

export default UserUpdateProfile;
