import React, { Component } from 'react';
import AppBody from '../../../../components/AppBody';

class UserUpdateProfile extends Component {

    constructor(props) {
        super(props);
        this.state = {
            name: '',
            email: '',
            phone: '',
            image: '',
            userDetails: []
        }
    }

    inputChangeHandler(e){this.setState({[e.target.name]:e.target.value})}

    render() {

        return (
            <AppBody
                content={
                    <>
                        <div className="container">
                            <div className="row">
                                <div className="col-md-3"></div>
                                <div className="col-md-6">
                                    <div className="card mt-5 mb-5 p-5">
                                        <div className="form-group text-center">
                                            <img className="img" src={this.state.image} style={{ backgroundColor: 'white', border: '1px solid black', borderRadius: '100px', height: '200px', width: '200px', cursor: 'pointer' }} onClick={this.imageClickHandler} />
                                            <input type="file" name="image" id="updateImage" onChange={e => { this.inputChangeHandler(e) }} />
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="name">Name</label>
                                            <input type="text" className="form-control" placeholder="Name" name="name" id="name" value={this.state.name} onChange={e => { this.inputChangeHandler(e) }} />
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="email">Email</label>
                                            <input type="text" className="form-control" name="email" placeholder="Email" id="email" value={this.state.email} onChange={e => { this.inputChangeHandler(e) }} />
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="phone">Mobile No.</label>
                                            <input type="text" className="form-control" id="phone" name="phone" placeholder="Phone" value={this.state.phone} onChange={e => { this.inputChangeHandler(e) }} />
                                        </div>
                                        <div className="form-group">
                                            <button className="form-control btn btn-warning" >Update</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </>
                }

            />
        )
    }
}

export default UserUpdateProfile;
