import React, { Component } from "react";
import Loader from 'react-loader-spinner'

class AppLoader extends Component {
  render() {
    const { loader } = this.props;
    return (
      <div>
        {loader && (
          <>
            <Loader
              type="Puff"
              color="#00BFFF"
              height={100}
              width={100}
              timeout={3000} //3 secs

            />
          </>
        )}
      </div>
    );
  }
}
export default AppLoader;
