import axios from 'axios';
import React, { Component } from 'react'
import { withRouter } from 'react-router';
import AppBody from '../../../../components/AppBody';
import helper from '../../../../utils/helper';
import TimelineBlogs from '../component/TimelineBlogs';

const user = helper.user();
class ShowBlogPage extends Component {

    constructor(props) {
        super(props);
        this.state = {
            loading: false,
            allBlogs:[],
        }
    }

    componentDidMount() {
        this.setState({loading:true})
        // axios.defaults.headers.common['Authorization'] = `Bearer ${user.access_token}`
       
        axios.get('http://indonews.live/api/get-blogs')
        .then(res=>{
            this.setState({loading:false,allBlogs:res.data.list})
            console.log(res)
        }).catch(err=>{
            this.setState({loading:false})
        })
    }

    render() {
        return (
            <AppBody
                loader={this.state.loading}
                content={
                    <>
                        <div className="container fluid mt-2 bg-secondary text-white">
                            <div className="row">
                                <h1>Blogs</h1>
                            </div>
                        </div>
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col">
                                    {
                                        this.state.allBlogs.map(blog=>{
                                            return <TimelineBlogs blog={blog} edit={false} />
                                        })
                                    }
                                </div>
                            </div>
                        </div>
                    </>
                }
            />
        )
    }
}
export default withRouter(ShowBlogPage);
