import React, { Component } from 'react'
import AppBody from '../../../../components/AppBody';
import axios from "axios";
import { Link } from "react-router-dom";
import helper from '../../../../utils/helper';
const user=helper.user();
class UserProfilePage extends Component {
    constructor(props) {
        super();
        this.state = {
            timelineBlogs: [],
            userDetails:[],
            userFollower:[],
            loading:false,
        }
    }
    componentDidMount(){      
         axios.get('http://indonews.live/api/profile',{
            headers:{"Authorization":`Bearer ${user.access_token}`}
        }).then(res=>{
            this.setState({userDetails:res.data,loading:false});
        }).catch(err=>{
            console.log(err);
            this.setState({loading:false});
        })

        axios.get('http://indonews.live/api/get-followers',{
            headers:{"Authorization":`Bearer ${user.access_token}`}
        }).then(res=>{
            this.setState({userFollower:res.data,loading:false});
        }).catch(err=>{
            console.log(err);
            this.setState({loading:false});
        })
     }
    render() {
         const {userDetails}=this.state;
        console.log(userDetails);
        return (
            <AppBody 
            loader={this.state.loading} 
            content={
                <div className="container-fluid">
                <div className="row" style={{ background: ' rgb(2,0,36)', background: 'linear-gradient(90deg, rgba(2,0,36,1) 0%, rgba(9,9,121,1) 35%, rgba(0,212,255,1) 100%)', height: '250px' }}>
                </div>
                <div className="row">
                    <div className="col-md-3 text-center">
                        {
                            this.state.userDetails.profile_pic != null ?
                            <img className="img" src={this.state.userDetails.profile_pic} style={{ backgroundColor: 'white', border: '1px solid black', borderRadius: '100px', height: '200px', width: '200px', marginTop: '-100px' }} />
                            :

                            <img className="img" src="https://cdn1.iconfinder.com/data/icons/avatars-55/100/avatar_profile_user_music_headphones_shirt_cool-512.png" style={{ backgroundColor: 'white', border: '1px solid black', borderRadius: '100px', height: '200px', width: '200px', marginTop: '-100px' }} />
                        }

                    </div>
                    <div className="col-md-2 mt-3">
                        <div className="row">
                            <div className="col-12">
                                <strong>{this.state.userDetails.name}</strong>
                            </div>
                            <div className="col-12">
                            {this.state.userDetails.email}
                            </div>
                            <div className="col-12">
                            {this.state.userDetails.mobile_no}
                            </div>
                        </div>
                    </div>
                    <div className="col-md-4">
                        <div className="row">
                            <div className="col mt-5" style={{ cursor: 'pointer' }}><Link to="/followers" style={{ textDecoration: 'none', textDecorationColor: 'black' }}><strong>Followers</strong> <span style={{ color: '#b3b3b3' }}>{this.state.userDetails.followers}</span></Link></div>
                            <div className="col mt-5" style={{ cursor: 'pointer' }}><Link to="/following" style={{ textDecoration: 'none', textDecorationColor: 'black' }}><strong>Following</strong> <span style={{ color: '#b3b3b3' }}>{this.state.userDetails.follow}</span></Link></div>
                        </div>
                    </div>
                    <div className="col-sm-12 col-md-3 mt-5">
                        <Link to="updateprofile" >
                            <button className="btn btn-outline-info form-control">
                                update profile
                        </button>
                        </Link>
                    </div>
                </div>
            </div>            
            }/>             
         )
    }
}
export default UserProfilePage;
